@use "./variables";

/*------------------------------*\
    #mixins 
\*------------------------------*/

// setting cutom media queries
@mixin respond-to($breakpoint) {
  @media screen and (min-width: map-get($map:variables.$Breakpoint,$key:$breakpoint)) {
    @content;
  }
}

// flex container
@mixin flexify($al-con: center, $js-con: space-between, $flex-d: row) {
  display: flex;
  align-items: $al-con;
  justify-content: $js-con;
  flex-direction: $flex-d;
}

